sys_script.script:
function onAfter(current, previous) {
  //This function will be automatically called when this rule is processed.
  gs.include("xMattersLogger");
  gs.include("xMattersGroupSync");
  gs.include("xMattersGroupMemberSync");

  appPrefix = gs.getCurrentScopeName();

  var log = new xMattersLogger(gs.getProperty(appPrefix + '.' + "xmatters.logging.level"));
  var groupSync = new xMattersGroupSync();
  var memberSync = new xMattersGroupMemberSync();
  var response = null;

  if ( gs.getProperty( appPrefix + '.' + "xmatters.sync.dynamically") == "true") {

    if (current.operation() == "update") {
      log.debug("Business rule to update group "+previous.name+" in xMatters");
      response = groupSync.syncGroup(current, previous.name, "UpdateGroup");

      log.debug("Business rule to update group. syncGroup response: " + response);

      if (!(''+response).startsWith("Group not synchronized")) {
        log.debug("Syncing Group Members");
        // Takes about 800 ms, so it's better do it asynchronously  
        //memberSync.syncMembership(current.name.toString());

        worker = new GlideScriptedHierarchicalWorker();
        worker.setProgressName("Group Member Sync Worker");
        worker.setBackground( true );
        // specify a script include and method to call, with whatever parameters you need
        worker.setScriptIncludeName( appPrefix + '.' + "xMattersGroupMemberSync");
        worker.setScriptIncludeMethod( "syncMembership" );
        worker.putMethodArg("groupName", current.name.toString());

        worker.start(); 
      }
    } else if (current.operation() == "insert") {
      log.debug("Business rule to add group "+current.name+" to xMatters");
      response = groupSync.syncGroup(current, current.name, "AddGroup");
      log.debug(response);
    } else if (current.operation() == "delete") {
      log.debug("Business rule to delete group "+current.name+" from xMatters");
      response = groupSync.syncGroup(current, current.name, "DeleteGroup");
      log.debug(response);
    } else {
      log.error("Business Rule 'xMatters Group Sync' ran during operation = "+current.operation()+". This should be considered an error.");
    }
  } else {
    log.debug("Group will not be synchronized as the 'Dynamic Sync' property is turned off");
  }
}
