sys_script_include.script:
var xMattersLogger = Class.create();

xMattersLogger.prototype = {

  initialize : function(logLevel, source ) {

    this.logLevel = "WARN";
    this.type = 'xMattersLogger';
    this.source = (source == null ? '' : source + ' ' );

    logLevel = (logLevel == null ? 'WARN' : logLevel );

    if (logLevel.toUpperCase() == "ERROR") {
      this.logLevel = 3;
    }
    else if (logLevel.toUpperCase() == "INFO") {
      this.logLevel = 1;
    }
    else if (logLevel.toUpperCase() == "DEBUG") {
      this.logLevel = 0;
    }
    else {
      this.logLevel = 2;  // Default is WARN
    }
  },

  isDebug : function() { 
    return this.logLevel <= 0; 
  },

  debug : function(s) {
    if (this.isDebug()){
      s = 'DEBUG: ' + ' ' + s;
      gs.debug( '{0}: {1}', new Date().getTime() + ': ', this.source + s );
    }
  },

  info : function(s) {
    if (this.logLevel <= 1){
      gs.info( '{0}: {1}', new Date().getTime() + ': ', this.source + s );
    }
  },

  warn : function(s) {
    if (this.logLevel <= 2){
      gs.warn( '{0}: {1}', new Date().getTime() + ': ', this.source + s );
    }
  },

  error : function(s) {
    if (this.logLevel <= 3){
      gs.error( '{0}: {1}', new Date().getTime() + ': ', this.source + s );
    }
  },

  paddy : function (n, p, c) {
    var pad_char = typeof c !== 'undefined' ? c : '0';
    var pad = new Array(1 + p).join(pad_char);
    return (pad + n).slice(-pad.length);
  }
};
